import closed_letter from './vecteezy_valentine-day-love-letter-envelope_22034135_962.png';
import open_letter from './old-sheet-of-paper-cartoon-with-curled-corner-stationery-paper-letter-vector.jpg';
import './App.css';
import React, { useState } from 'react';

function App() {
  const [imageVisible, setImageVisible] = useState(true);

  const handleClick = () => {
    setImageVisible(!imageVisible);
  };

  return (
    <header className="App-header">
    <div className="App-logo">
        <img src={closed_letter}
          alt="logo" 
          onClick={handleClick}  
          className={imageVisible ? "fade-in" : "fade-out"}/>
        <img 
          src={open_letter} 
          alt="letter"
          className={imageVisible ? "fade-out" : "fade-in"} 
        />
    </div>
    </header>
  );
}


export default App;
