import logo from './vecteezy_valentine-day-love-letter-envelope_22034135_962.png';
import letter from './result.png'
import './App.css';
import React, { useState, useEffect } from 'react';

function App() {
  const [imageVisible, setImageVisible] = useState(true);
  const [animationPaused, setAnimationPaused] = useState(false);
  const [text, setText] = useState('');
  const fullText = 'Happy six months baby.';
  let i = 0;

  const typeWriter = () => {
    if (i < fullText.length) {
      setText((prev) => prev + fullText.charAt(i));
      i++;
      setTimeout(typeWriter, 50);
    }
  };

  useEffect(() => {
    typeWriter();
  }, []);

  useEffect(() => {
    if (!imageVisible) {
      console.log("The image is now invisible!");
    }
  }, [imageVisible]);

  const handleClick = () => {
    if (imageVisible && !animationPaused) {
      setAnimationPaused(true);
      requestAnimationFrame(() => {
        setImageVisible(false);
      });
    }
  };

  return (
    <header className="App-header">
      <div class="card">
        <div class="left-card">
            <div class="card-front">
                <img src="card-front.png" alt="two worms looking at the sunset"/>
            </div>
            <div class="card-back">
            </div>
        </div>
        <div class="right-card">
            <h2>I love you so much! 
            </h2>
        </div>
      </div>


      <div className="App" onClick={handleClick}>
        <img 
          src={logo} 
          className={`${imageVisible ? "App-logo-fade-in" : "App-logo-fade-out"} ${animationPaused ? "App-logo-paused" : ""}`} 
          alt="logo" 
        />
        <div className={imageVisible ? "Letter-Out" : "Letter-In"}>
          <img src={letter} alt="letter" />
          <div className="Typewriter-Text">{text}</div>
        </div>
      </div>
    </header>
  );
}
