import logo from './vecteezy_valentine-day-love-letter-envelope_22034135_962.png';
import letter from './old-sheet-of-paper-cartoon-with-curled-corner-stationery-paper-letter-vector.jpg'
import './App.css';
import React, { useState, useEffect } from 'react';

function App() {
  const [imageVisible, setImageVisible] = useState(true);
  const [animationPaused, setAnimationPaused] = useState(false);

  const handleClick = () => {
    if (imageVisible && !animationPaused) {
      setAnimationPaused(true);
      requestAnimationFrame(() => {
        setImageVisible(false);
      });
    }
  }

  useEffect(() => {
    if (!imageVisible) {
      console.log("The image is now invisible!");
    }
  }, [imageVisible]);


  return (
    <header className="App-header">
    <div className="App" onClick={handleClick}>
            <img src={logo} 
              className={`${imageVisible ? "App-logo-fade-in" : "App-logo-fade-out"} ${animationPaused ? "App-logo-paused" : ""}`} 
              alt="logo" 
            />
            <img src={letter}
              className={imageVisible ? "Letter-In" : "Letter-Out"}
          />
    </div>
    </header>
  );
}

export default App;
